/**
 * Centralized Error Messages
 *
 * This file contains all error messages used throughout the contentstack-js-core SDK.
 * Centralizing error messages makes them easier to maintain, review, and localize.
 */
export declare const ERROR_MESSAGES: {
    readonly CONSOLE: {
        readonly ERROR_WITH_TITLE: (title: string) => string;
        readonly LEVEL_WITH_DATA: (level: string, data: any) => string;
    };
    readonly API: {
        readonly NETWORK_ERROR: "Network error occurred. Please check your internet connection and try again.";
        readonly UNKNOWN_ERROR: "An unknown error occurred. Please try again or contact support if the issue persists.";
        readonly REQUEST_FAILED: (status?: number) => string;
    };
    readonly REQUEST: {
        readonly HOST_REQUIRED_FOR_LIVE_PREVIEW: "Host is required for live preview. Please provide a valid host parameter in the live_preview configuration.";
    };
    readonly RETRY: {
        readonly TIMEOUT_EXCEEDED: (timeout: number) => string;
    };
    readonly ERROR_CODES: {
        readonly NETWORK_ERROR: "NETWORK_ERROR";
        readonly UNKNOWN_ERROR: "UNKNOWN_ERROR";
        readonly TIMEOUT: 408;
    };
};
