/**
 * Centralized error messages for the Contentstack SDK
 * This file contains all user-facing error messages used throughout the SDK
 */
declare const ErrorMessages: {
    readonly INVALID_FIELD_UID: "Invalid fieldUid. Provide an alphanumeric field UID and try again.";
    readonly INVALID_KEY: "Invalid key. Provide an alphanumeric key and try again.";
    readonly INVALID_REFERENCE_UID: (uid: string) => string;
    readonly INVALID_VALUE_STRING_OR_NUMBER: "Invalid value. Provide a string or number and try again.";
    readonly INVALID_VALUE_ARRAY: "Invalid value. Provide an array of strings, numbers, or booleans and try again.";
    readonly INVALID_ARGUMENT_STRING_OR_ARRAY: "Invalid argument. Provide a string or an array and try again.";
    readonly MISSING_CUSTOM_STORAGE: "Missing storage for customStorage. Provide a storage object with get, set, and remove methods and try again.";
    readonly INVALID_REGEX_PATTERN: "Invalid regexPattern: Must be a valid regular expression";
    readonly SLACK_MESSAGE_FAILED: (details?: string) => string;
    readonly SLACK_FAILURE_DETAILS_FAILED: (details?: string) => string;
};
/**
 * Error codes for programmatic error handling
 */
declare enum ErrorCode {
    INVALID_FIELD_UID = "INVALID_FIELD_UID",
    INVALID_KEY = "INVALID_KEY",
    INVALID_REFERENCE_UID = "INVALID_REFERENCE_UID",
    INVALID_VALUE = "INVALID_VALUE",
    INVALID_ARGUMENT = "INVALID_ARGUMENT",
    MISSING_STORAGE = "MISSING_STORAGE",
    INVALID_REGEX = "INVALID_REGEX",
    SLACK_ERROR = "SLACK_ERROR"
}

export { ErrorCode, ErrorMessages };
