import { queryParams, PaginationObj } from './types.js';
import '@contentstack/core';
import '../persistance/types/storage-type.js';
import '../persistance/config/persistance-storage-config.js';
import '../persistance/types/storage.js';
import '../persistance/helper/utils.js';

declare class Pagination {
    _queryParams: queryParams;
    /**
     * @method constructor
     * @memberof Pagination
     * @description Create a pagination class object to paginate through the query response
     * @param {BaseQuery} object of class BaseQuery
     * @param {PaginationObj} object to send skip and limit values
     * @example
     * import contentstack from '@contentstack/delivery-sdk'
     *
     * const stack = contentstack.stack({ apiKey: "apiKey", deliveryToken: "deliveryToken", environment: "environment" });
     * const query = stack.contentType("contentTypeUid").entry().query();
     *
     * interface TEntryProps {
     *   uid: string;
     *   title: string;
     *   created_at: string;
     *   _version: number;
     *   locale: string;
     *   created_by: string;
     *   url: string;
     *   // other props
     * }
     * interface TEntries {
     *   entries: TEntryProps[];
     * }
     * const pagedResult = await query.paginate().find<TEntries>();
     * OR
     * const pageObj = await query.paginate({ skip: 10, limit: 20 }).find<TEntries>();
     * @returns {Pagination}
     */
    paginate(paginationObj?: PaginationObj): this;
    /**
     * @method next
     * @memberof Pagination
     * @description Go to next set of response values - skips the current num of responses and shows next responses
     * @example
     * import contentstack from '@contentstack/delivery-sdk'
     *
     * const stack = contentstack.stack({ apiKey: "apiKey", deliveryToken: "deliveryToken", environment: "environment" });
     * const query = stack.contentType("contentTypeUid").entry().query();
     *
     * interface TEntryProps {
     *   uid: string;
     *   title: string;
     *   created_at: string;
     *   _version: number;
     *   locale: string;
     *   created_by: string;
     *   url: string;
     *   // other props
     * }
     * interface TEntries {
     *   entries: TEntryProps[];
     * }
     * const pagedResult = await query.paginate().find<TEntries>();
     * const nextPageResult = await query.next().find<TEntries>();
     * @returns {Object}
     */
    next(): this;
    /**
     * @method previous
     * @memberof Pagination
     * @description Go to previous set of response values - skips the current num of responses and shows previous responses
     * @example
     * import contentstack from '@contentstack/delivery-sdk'
     *
     * const stack = contentstack.stack({ apiKey: "apiKey", deliveryToken: "deliveryToken", environment: "environment" });
     * const query = stack.contentType("contentTypeUid").entry().query();
     *
     * interface TEntryProps {
     *   uid: string;
     *   title: string;
     *   created_at: string;
     *   _version: number;
     *   locale: string;
     *   created_by: string;
     *   url: string;
     *   // other props
     * }
     * interface TEntries {
     *   entries: TEntryProps[];
     * }
     * const pagedResult = await query.paginate().find<TEntries>();
     * const prevPageResult = await query.previous().find<TEntries>();
     * @returns {Pagination}
     */
    previous(): this;
}

export { Pagination };
